import React, { useState } from 'react';
import axios from 'axios';

function ConsultaEstudiantes() {
  const [carnet, setCarnet] = useState('');
  const [nombres, setNombres] = useState('');
  const [correo, setCorreo] = useState('');
  const [seccion, setSeccion] = useState('');
  const [error, setError] = useState('');

  const handleBuscar = async () => {
    try {
      const response = await axios.get(`https://test-deploy-12.onrender.com/estudiantes/${carnet}`);
      setNombres(response.data.nombres || 'No encontrados');
      setCorreo(response.data.correo || 'No encontrados');
      setSeccion(response.data.seccion || 'No encontrados');
      setError('');
    } catch (err) {
      setNombres('No encontrados');
      setCorreo('No encontrados');
      setSeccion('No encontrados');
      setError('Error al buscar el estudiante. Verifique el número de carnet.');
    }
  };

  const handleLimpiar = () => {
    setCarnet('');
    setNombres('');
    setCorreo('');
    setSeccion('');
    setError('');
  };

  const handleCancelar = () => {
    setCarnet('');
    setNombres('');
    setCorreo('');
    setSeccion('');
    setError('');
  };

  const buttonStyle = {
    backgroundColor: '#00BFFF', // Celeste
    color: '#fff', // Texto en blanco
    border: 'none',
    padding: '10px 20px',
    borderRadius: '5px',
    cursor: 'pointer',
    margin: '5px',
  };

  return (
    <div>
      <h1>Consulta de Alumnos</h1>
      <label>
        Carnet:
        <input 
          type="text" 
          value={carnet} 
          onChange={(e) => setCarnet(e.target.value)} 
        />
      </label>
      <br />
      <label>
        Nombres:
        <input 
          type="text" 
          value={nombres} 
          readOnly 
        />
      </label>
      <br />
      <label>
        Correo Electrónico:
        <input 
          type="text" 
          value={correo} 
          readOnly 
        />
      </label>
      <br />
      <label>
        Sección:
        <input 
          type="text" 
          value={seccion} 
          readOnly 
        />
      </label>
      <br />
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <button style={buttonStyle} onClick={handleBuscar}>Buscar</button>
      <button style={buttonStyle} onClick={handleLimpiar}>Limpiar</button>
      <button style={buttonStyle} onClick={handleCancelar}>Cancelar</button>
    </div>
  );
}

export default ConsultaEstudiantes;
