import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [carnet, setCarnet] = useState('');
  const [nombres, setNombres] = useState('');
  const [correo, setCorreo] = useState('');
  const [seccion, setSeccion] = useState('');
  const [mensaje, setMensaje] = useState('');

  const handleBuscar = async () => {
    if (!carnet) {
      setMensaje('Por favor, ingresa un número de carnet.');
      return;
    }

    try {
      const response = await axios.get(`https://test-deploy-12.onrender.com/estudiantes/${carnet}`);
      const data = response.data;

      if (data) {
        setNombres(data.nombres || 'No disponible');
        setCorreo(data.correo || 'No disponible');
        setSeccion(data.seccion || 'No disponible');
        setMensaje('Datos encontrados correctamente.');
      } else {
        setMensaje('No se encontraron datos para el carnet ingresado.');
        handleLimpiarCampos();
      }
    } catch (error) {
      console.error(error);
      setMensaje('Error al conectar con la API. Verifica el número de carnet e intenta nuevamente.');
      handleLimpiarCampos();
    }
  };

  const handleLimpiarCampos = () => {
    setCarnet('');
    setNombres('');
    setCorreo('');
    setSeccion('');
  };

  const handleCancelar = () => {
    handleLimpiarCampos();
    setMensaje('');
  };

  return (
    <div>
      <h1>Consulta de Alumnos</h1>
      <label>
        Carnet:
        <input
          type="text"
          value={carnet}
          onChange={(e) => setCarnet(e.target.value)}
          placeholder="1590-19-16085"
        />
      </label>
      <button onClick={handleBuscar}>Buscar</button>
      <button onClick={handleLimpiarCampos}>Limpiar</button>
      <button onClick={handleCancelar}>Cancelar</button>

      {mensaje && <p>{mensaje}</p>}
      <div>
        <p>Nombres: {nombres}</p>
        <p>Correo Electrónico: {correo}</p>
        <p>Sección: {seccion}</p>
      </div>
    </div>
  );
};

export default App;
